import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Header1, Header3 } from './styles/Headers';
import Today from './components/Today';
import { lightTheme, darkTheme } from './styles/themeing';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);

  return (
    <Wrapper>
      <Content
        initial={{ opacity: 0, y: 15 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <NameInput
          margin='0.75rem'
          placeholder='Insert Name here'
          /*defaultValue={window.localStorage.getItem('name')}
          onChange={(e) => {
            window.localStorage.setItem('name', e.target.value);
          }}*/
        />
        <Today />
        <ItemGrid>
          <Header3>test1</Header3>
          <Header3>test2</Header3>
          <Header3>test3</Header3>
          <Header3>test4</Header3>
          <Header3>test5</Header3>
          <Header3>test6</Header3>
        </ItemGrid>
        <button
          onClick={() => {
            setIsDarkMode(!isDarkMode);
          }}
        >
          Switch Mode
        </button>
      </Content>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
const Content = styled(motion.section)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
const ItemGrid = styled.section`
  display: grid;
  grid-template-columns: auto auto;
  grid-template-rows: auto auto auto;
  @media only screen and (min-width: 500px) {
    grid-template-columns: auto auto auto;
    grid-template-rows: auto auto;
  }
`;

const NameInput = styled.input`
  border: none;
  background: none;
  margin: ${(props) => (props.margin ? props.margin : '1rem')};
  font-family: 'Airbnb Cereal App';
  font-size: 3.25em;
  font-weight: bold;
  text-align: center;
`;

export default App;
